mapper.update:
update PROJECT set
UNIT_NAME= #{unitName,jdbcType=VARCHAR}, PROJ_ADDR= #{projAddr,jdbcType=VARCHAR}, PROJ_UNIT_NAME= #{projUnitName,jdbcType=VARCHAR}, PROJ_CONDITION= #{projCondition,jdbcType=VARCHAR}, PROJ_DUTY= #{projDuty,jdbcType=VARCHAR}, PROJ_CONTACTS= #{projContacts,jdbcType=VARCHAR}, PROJ_POSITION= #{projPosition,jdbcType=VARCHAR},
START_DATE = to_date(#{startDateStr},'yyyy-MM-dd'),
END_DATE = to_date(#{endDateStr},'yyyy-MM-dd'),
BUILDER= #{builder,jdbcType=VARCHAR}, REMARK= #{remark,jdbcType=VARCHAR}, MODIFY_NAME = #{modifyName,jdbcType=VARCHAR}, MODIFY_DATE = sysdate
,PROJ_TEL=#{projTel}
,PROJ_ADDRESS=#{projAddress}
where ID = #{id,jdbcType=VARCHAR}

mapper.select:
select
, to_char(START_DATE,'yyyy-mm-dd') as startDateStr,
to_char(END_DATE,'yyyy-mm-dd') as endDateStr, to_char(CREATE_DATE,'yyyy-mm-dd') as createDateStr from PROJECT where DELETE_FLAG='0' and id=#{id}

mapper.update:
update PROJECT_LINK set
GT_TIME=TO_DATE(#{gtTime,jdbcType=VARCHAR}, 'yyyy-MM-dd hh24:mi:ss' ),
PROJECT_ID=#{projectId,jdbcType=VARCHAR}, GT_MEN=#{gtMen,jdbcType=VARCHAR}, CUSATOMER= #{custaomer,jdbcType=VARCHAR}, GT_CODE=#{gtCode,jdbcType=VARCHAR}, GT_ADDRESS=#{gtAddress,jdbcType=VARCHAR}, GT_CONTEXT= #{ gtContext,jdbcType=VARCHAR} where ID=#{id,jdbcType=VARCHAR}
